import React, { Component } from 'react';
import axios from 'axios';

export default class PostInput extends Component {
    constructor(props){
        super(props);
        this.state={
            title:'',
            text:'',
            file: null,
            user: JSON.parse(localStorage.getItem('user'))
        }
        this.formRef = React.createRef();
        // let service = axios.create({
        //     baseURL: `${process.env.REACT_APP_API}`,
        //     withCredentials: true
        //   });
        // this.service=service;
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
        const form = new FormData(this.formRef.current)
        axios({
            method: "POST",
            baseURL: `${process.env.REACT_APP_API}/post`,
            data: form,
            withCredentials:true
        })
        .then(response=>{
            this.props.history.push('/share/thoughtsCorner')
        })
        .catch(err =>{
            console.log(err)
        })
      }

    handleChange = (event) => {  
    const {name, value} = event.target;
    this.setState({[name]: value});
    }

    uploadPic=(event)=>{
        this.setState({
          file: (event.target.files[0])})
      }

    render() {
        return (
            <div className="postInput">
            <form ref={this.formRef} onSubmit={this.handleFormSubmit}>
                <input name="title" placeholder="title" value={this.state.title} onChange={this.handleChange} type="text"/>
                <textarea className="form-control" name="text" cols="40" rows="5" placeholder="Share your thought..." value={this.state.text} onChange={this.handleChange}></textarea>
                <input type="file" name="image" onChange={this.uploadPic}/>
                <button style={{marginTop:"10px"}} type="submit">Share</button>
            </form>
            </div>
        )
    }
}
