import React, { Component } from 'react';
import Chatkit from '@pusher/chatkit';
import MessageList from './MessageList';

export default class ChatRoom extends Component {
    constructor(){
        super()
        this.state={
            currentScreen: "UserScreen",
            messages:[]
        }
    }
    componentDidMount (){
        const chatManager = new Chatkit.ChatManager({
            instanceLocator: process.env.REACT_APP_chatkit_instance_locator,
            userId: this.props.userInSession.username,
            tokenProvider: new Chatkit.TokenProvider({
                url:"https://us1.pusherplatform.io/services/chatkit_token_provider/v1/95077b15-c43c-4d68-ae92-7a1f082f91c8/token"
            })
        })

        chatManager.connect()
        .then( currentUser=>{
            currentUser.createRoom({
                name: 'General',
                private: true,
                addUserIds: ['craig', 'kate'],
                customData: { foo: 42 },
              })
              .then(room => {
                  return currentUser.subcribeToRoom({
                      roomId:room.id,
                      messageLimit:100,
                      hooks:{
                          onNewMessage: message=>{
                              this.setState({
                                  messages: [...this.state.messages,message]
                              })
                          }
                      }
                  })
                // console.log(`Created room called ${room.name}`)
              })
              .catch(err => {
                console.log(`Error creating room ${err}`)
              })
        })

    }
    render() {
        return (
            <div>
                <MessageList messages={this.state.messages}/>
            </div>
        )
    }
}
