import {Link} from 'react-router-dom';
import axios from 'axios';
import './Inbox.css';

export default class AllRooms extends Component {
    constructor(props){
        super(props);
        this.state={
            chatRooms:[]
        };
    }

    componentDidMount (){
        axios.get(`${process.env.REACT_APP_API}/inbox`, {withCredentials:true})
        .then(response => {
                let chatRooms= response.data;
                    this.setState({chatRooms:chatRooms})
    })
    }

    render() {
        let eachRoom= this.state.chatRooms.map(room=>{
            console.log(room)
            return(
                <Link to={{ pathname: `/inbox/${room.id}`,
