import React, { Component } from 'react';
import axios from 'axios';
import {Link, Redirect} from 'react-router-dom';
import {formatWrittenAt} from '../../method'; 

export default class EachPost extends Component {
    constructor(props){
        super(props);
        this.state={
            user:JSON.parse(localStorage.getItem('user')),
            comment:'',
            post:null
        };
        this.formatWrittenAt= formatWrittenAt.bind(this)
    }

    componentDidMount(){
        const { params } = this.props.match;
        axios({
            method:"GET",
            baseURL:`${process.env.REACT_APP_API}/post/${params.id}`,
            withCredentials:true
        })
        .then(response=>{
            console.log(response.data)
            let now = new Date();
            let post = response.data.post;
            let comments = response.data.comments;
            let eachComment= comments.map(comment=>{
                comment.niceTime= this.formatWrittenAt(new Date(post.writtenAt), now);
                return comment
            })
                post.niceTime = this.formatWrittenAt(new Date(post.writtenAt), now);
            this.setState({post:post, comments:eachComment})
        })
        .catch(err=>{
            console.log(err)
        })
    }

    deletePost (postId){
        axios({
            method:"POST",
            baseURL:`${process.env.REACT_APP_API}/deletePost/${postId}`,
            withCredentials:true
        })   
        .then(()=>{
            this.props.history.push('/');

            // this.props.history.push("/share/thoughtsCorner/allThoughts")
            // return <Redirect to="/share/thoughtsCorner/allThoughts"/>
        })
        .catch(err=>{
            console.log(err)
        })
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
        const comment = this.state.comment;
        const user=this.state.user;
        axios.post(`${process.env.REACT_APP_API}/post/${this.state.post._id}/comment`,{comment, user})
        .then(response=>{ 
            let now = new Date();
            let post=response.data;
            post.niceTime = this.formatWrittenAt(new Date(post.writtenAt), now);
            this.setState({comment:"", post:post})
            // this.props.history.push(`/share/thoughtsCorner/allThoughts/${this.state.post._id}`)    
        })
        .catch(err =>{
            console.log(err)
        })
      }

    handleChange = (event) => {  
    const {name, value} = event.target;
    this.setState({[name]: value});
    }

    render() {
        if(this.state.post){
            let eachLine= this.state.post.text.map(text=>{
                return(
                    <p className="eachLine">{text}</p>
                )
            })
            let eachComment;
            if(this.state.comments){
                eachComment= this.state.comments.map(comment=>{
                    return(
                        <>
                        <p className="eachLine">{comment.text}</p>
                        <p className="eachLine">by {comment.author.username} at {comment.niceTime}</p>
                        </>
                    )
                })
            }
            return (
            <div className="postDetails">
                <h3 className="eachLine">{this.state.post.title}</h3>
                    <div className="eachMesDiv" style={{backgroundImage: `url(${this.state.post.author.profilePicUrl})`, width:"40px", height:"40px"}}></div>
                    {this.state.post.author._id === this.state.user._id?
                        (<>
                            <p style={{textAlign:"left"}}>Posted by you</p>
                            <p className="eachLine" style={{marginTop:"10px", color:"darkgrey !important"}}>{this.state.post.niceTime}</p>
                            <div className="rowFlex" style={{justifyContent:"flex-start"}}>
                                <Link style={{marginRight:"10px"}} exact to={{pathname: `/share/thoughtsCorner/allThoughts/editThought/${this.state.post._id}`}}>Edit</Link>
                                <Link style={{marginRight:"10px"}} 
                                onClick={this.deletePost(this.state.post._id)} 
                                exact to="/delete"
                                // exact to={{pathname:`/share/thoughtsCorner/allThoughts/delete/${this.state.post._id}`}}
                                // to={{pathname: `/share/thoughtsCorner/allThoughts/delete/${this.state.post._id}`}}
                                >Delete</Link>
                            </div>
                        </>
                        ) 
                        :   
                        (<>
                            <p className="eachLine">Posted by <Link to={{ pathname: `/nearby/${this.state.post.author._id}`}}>{this.state.post.author.username}</Link>            
                            </p>
                            <p className="eachLine" style={{marginTop:"10px", color:"darkgrey !important"}}>{this.state.post.niceTime}</p>
                        </>
                        )
                    }
                <hr/>
                {eachLine}
                <hr/>
                <form onSubmit={this.handleFormSubmit}>
                    <textarea className="form-control" name="comment" cols="40" rows="5" placeholder="Comment..." value={this.state.comment} onChange={this.handleChange}></textarea>
                    <button style={{marginTop:"10px", float:"left"}} type="submit">Comment</button>
                </form>
                <div>
                {eachComment}
                </div>
                <hr/>

            </div>
        )
        }
        else{
            return(
                <>
                </>
            )
        }

    }
}
