    //         this.props.history.push('/profile')
    //     })
    //     .catch( error => console.log(error) )
      }

    handleChange = (event) => {  
    const {name, value} = event.target;
    this.setState({[name]: value});
    }

    render() {
        return (
            <>
            <form onSubmit={this.handleFormSubmit}>
                <textarea className="form-control" name="text" cols="40" rows="5" placeholder="Share your thought..." value={this.state.text} require onChange={this.handleChange}></textarea>
                <button type="submit">Share</button>
            </form>
            </>
        )
    }
}
