import React, { Component } from 'react';
import axios from 'axios';

export default class EachPost extends Component {
    constructor(props){
        super(props);
        this.state={}
    }

    componentDidMount(){
        const { params } = this.props.match;
        debugger
        axios({
            method:"GET",
            baseURL:`${process.env.REACT_APP_API}/post/${params.id}`,
            withCredentials:true
        })
        .then(response=>{
            console.log(response.data)
            this.setState({post:response.data})
            let now = new Date();
            let changePost = [...this.state.post];
            if (changePost.text[0].length > 100) {
                changePost.short = []
                changePost.short[0] = changePost.text[0].substring(0, 100) + "..."
            } else if (changePost.text.length > 1) {
                changePost.short = []
                changePost.short[0] = post.text[0]
                changePost.short[1] = "..."
            }
            changePost.niceTime = this. formatWrittenAt(changePost.writtenAt, now);
            console.log(changePost)
        })
        .catch(err=>{
            console.log(err)
        })
    }

    render() {
        if(this.state.post){
            return (
            <div className="postDetails">
                <div className="eachMesDiv" style={{backgroundImage: `url(${this.state.post.author.profilePicUrl})`}}></div>
                <h3>{this.state.post.title}</h3>
                <p>Posted by {this.state.post.author.username} on {this.state.post.niceTime}</p>
                <p>{this.state.post.text}</p>
            </div>
        )
        }
        else{
            return(
                <>
                </>
            )
        }

    }
}
