import React, { Component } from 'react';
import axios from 'axios';

export default class NearbyUser extends Component {
    constructor(props){
        super(props);
        this.state={}
    }

    componentDidMount(){
        const { params } = this.props.match;
        axios.get(`http://localhost:5000/api/nearby/${params.id}`, {withCredentials:true})
        .then( response =>{
          const user = response.data;
          this.setState(user);
        })
        .catch((err)=>{
            console.log(err)
        })
    }


    render() {
        return (
            <div className="NearbyUser">
                <div className="avaPic" style={{backgroundImage:`url(${this.state.profilePicUrl})` }}>
                </div>
                <h3>{this.state.username}</h3>
                <p><i className="fas fa-map-marker-alt"></i> {this.props.location.nearbyUserProps.distance}km</p>
                <hr/>
                <p>{this.state.about}</p>
            </div>
        )
    }
}
