import React, { Component } from 'react';
import {geolocated} from 'react-geolocated';
import axios from 'axios';

export default class Nearby extends Component {
    constructor(props){
        super(props);
        this.state={
            users: [],
            sortedUsers:[]
        }
    }
    componentDidMount() {
        debugger
          axios.get(`http://localhost:5000/api/nearby`)
          .then(response => {
              console.log(response.data)
              this.setState({users: response.data});
          })
          .catch(error=>{
              console.log(error)
          })
      }

      calculateDistance (lat1, lon1, lat2, lon2, unit) {
        var radlat1 = Math.PI * lat1/180
        var radlat2 = Math.PI * lat2/180
        var radlon1 = Math.PI * lon1/180
        var radlon2 = Math.PI * lon2/180
        var theta = lon1-lon2
        var radtheta = Math.PI * theta/180
        var dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
        dist = Math.acos(dist)
        dist = dist * 180/Math.PI
        dist = dist * 60 * 1.1515* 1.609344
        return dist
      }

      calculateUserDist(){
        let users=[...this.state.users];
        for (var i = 0; i < users.length; i++) {
            users[i]["distance"] = this.calculateDistance(users[0]["latitude"],users[0]["longitute"],users[i]["latitude"],users[i]["longitute"]);}
        users.sort(function(a, b) { 
            return a.distance - b.distance;
        });
        this.setState({sortedUsers:users})
    } 

    render() {
        if(!this.state.sortedUsers){
            this.calculateUserDist();
        }
        const users = this.state.sortedUsers;
        let eachUser=users.map(user=>{
            return(
                <div>
                    <h4>{user.username}</h4>
                </div>
            )
        })
        return (
            <div>
              {eachUser}  
            </div>
        )
    }
}
